import { IStyle } from './IStyle';
/**
 * Allows you to pass in 1 or more sets of areas which will return a merged
 * set of classes.
 *
 * @public
 */
export declare function mergeStyleSets<K extends string>(...cssSets: ({
    [P in K]?: IStyle;
} | null | undefined)[]): {
    [P in K]: string;
};
